package com.lms;

public class TriangleArray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
int[][] a= new int[10][10];
for(int i=0;i<10;i++) {
	for(int j=0;j<=i;j++) {
		a[i][j]=0;
		System.out.print(a[i][j]);
		
	}
	System.out.println();
}

	}

}
